import React, { useState } from 'react';
// Bootstrap
import { Button, Modal } from 'react-bootstrap';
// Materialize-UI Icons
import GitHubIcon from '@material-ui/icons/GitHub';
import InfoIcon from '@material-ui/icons/Info';
import LaunchIcon from '@material-ui/icons/Launch';
// Components
import { ProjectCarousel } from '../ProjectCarousel';

import './style.css';
export default function ProjectModal({hoverState, data}) {
  const [show, setShow] = useState(false);

  const handleClose = () => {
    hoverState(false);
    setShow(false);
  }
  const handleShow = () => {
    hoverState(true);
    setShow(true);
  }
  return (
    <>
      {console.log(data)}
      <Button variant="outline-light" onClick={handleShow}>
        Learn more
      </Button>

      <Modal 
        show={show} 
        onHide={handleClose}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        style={{color: '#fff'}}
        centered
      >
        <Modal.Body style={{background: '#181818'}}>
          <Modal.Title>{data.name}</Modal.Title>
          <p>{data.description}</p>
          <ProjectCarousel images={data.images}/>
        </Modal.Body>
        <Modal.Footer style={{background: '#121212'}}>
          <GitHubIcon className='mr-1'/>
          <div className='mr-auto'>
            <a 
              href={data.links.github} target='_blank'
              style={{ color: '#fff'}}
            >
              GitHub
            </a>
          </div>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant='outline-light' onClick={handleClose}>
            <LaunchIcon />
            Website
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
}
