import React from 'react';
import { Link } from "react-router-dom";
import { Navbar, Nav, Badge, Image, Button } from 'react-bootstrap';
import logoWhite from '../../assets/svg/logo-white.svg';
import logo from '../../assets/svg/logo.svg';
import './style.css';

const styles = {
  brand: {
    fontSize: '36px'
  },
  links: {
    fontSize: '20px',
  },
  background: {
    background: '#121212'
  }
}

export const Navigation = () => {
  return (
    <Navbar variant='dark' expand='true' style={styles.background}>
      <Navbar.Brand className='brand-name' style={styles.brand}>
        <Link to='/' style={{textDecoration: 'none', color: '#fff'}}>
          <Image src={logo} style={{width: '100px', fill: 'red'}} className='mb-1'/>
        </Link>
      </Navbar.Brand>
      {/* <Navbar.Brand className='brand-name' style={styles.brand}>
        <Link to='/' style={{textDecoration: 'none', color: '#fff'}}>Edward Reyes</Link>
      </Navbar.Brand> */}
      <Navbar.Toggle aria-controls="basic-navbar-nav" />
      <Navbar.Collapse id="basic-navbar-nav" className='d-flex justify-content-center'>
        <Nav className='ml-auto navigation-links' style={{textTransform: 'uppercase'}}>
          {/* <Nav.Link style={styles.links}>
            <Link to='/' style={{textDecoration: 'none', color: '#fff'}}>
              Home
            </Link>
          </Nav.Link> */}
          <Nav.Link style={styles.links}>
            <Link to='/projects' style={{textDecoration: 'none', color: '#fff'}}>
              <Button variant="outline-light" style={{width: '75%'}}>
                <span style={{fontSize: '20px'}}>Project</span>
              </Button>
            </Link>
          </Nav.Link>
          <Nav.Link style={styles.links}>
            <Link to='/contact' style={{textDecoration: 'none', color: '#fff'}}>
              <Button variant="outline-light">
                <span style={{fontSize: '20px'}}>Say Hello</span>
              </Button>
            </Link>
          </Nav.Link>
        </Nav>
      </Navbar.Collapse>
    </Navbar>
  )
}
