import React from 'react';
import { Link } from "react-router-dom";
import { Navbar, Nav, Badge } from 'react-bootstrap';
import './style.css';

const styles = {
  brand: {
    fontSize: '36px'
  },
  links: {
    fontSize: '20px',
  }
}

export const Navigation = () => {
  return (
    <Navbar bg='dark' variant='dark' style={{textTransform: 'uppercase'}}>
      <Navbar.Brand href='#home' className='brand-name' style={styles.brand}>Edward Reyes</Navbar.Brand>
      <Nav className='mr-auto navigation-link'>
        <Nav.Link style={styles.links}>
          <Link to='/' 
            style={{textDecoration: 'none', color: '#fff'}}
          >
            <span className='navigation-link'>Home</span>
          </Link>
        </Nav.Link>
        <Nav.Link style={styles.links}>
          <Link to='/portfolio' style={{textDecoration: 'none', color: '#fff'}}>
            Portfolio
          </Link>
        </Nav.Link>
        <Nav.Link style={styles.links}>
          <Link to='/contact' style={{textDecoration: 'none', color: '#fff'}}>
            Contact
          </Link>
        </Nav.Link>
      </Nav>
    </Navbar>
  )
}
