import React, { useState } from 'react';
import { Button, Card } from 'react-bootstrap';

// Components
import Backdrop from '../Backdrop';
import Modal from '../Modal';
import abstract from '../../assets/images/jumbotron-wallpaper.jpg';

import './style.css';

const someHandler = () => {

}

export default function ProjectCard() {

  const [hover, setHover] = useState(false);

  const toggleHover = () => {
    setHover(!hover);
  }

  let cardStyle;
  if (hover) {
    cardStyle = {
      background: 'rgba(0,0,0,0.7)', 
      transition: `${hover ? '0.5s' : '0.5s;'}`
    }
  } else {
    cardStyle = {
      background: 'rgba(0,0,0,0)', 
      transition: `${hover ? '0.5s' : '0.5s;'}`,
      padding: 0
    }
  }

  return (
    <Card className="text-white">
      <Card.Img src={abstract} alt="Card image" />
      <Card.ImgOverlay className='image-overlay' 
        style={cardStyle}
        onMouseEnter={toggleHover} onMouseLeave={toggleHover}  
      >
        {hover ? (<>
          <Card.Title>Card title</Card.Title>
          <Card.Text>
            This is a wider card with supporting text below as a natural lead-in to
            additional content. This content is a little bit longer.
          </Card.Text>
          <Card.Text>Last updated 3 mins ago</Card.Text>
          <Button>Learn more</Button>
        </>) : (
          <Card.Title style={{background: 'red'}}>Project Title</Card.Title>
        )}

      </Card.ImgOverlay>
    </Card>
  );
}
